const correlation = (xs, ys) => {
  let n = Math.min(xs.length, ys.length)
  let [s_x, s_y, s_xy, s_x2, s_y2] = [0,0,0,0,0] 
  for (let i = 0; i < n; i++) {
    s_x += xs[i];    s_x2 += xs[i] * xs[i];
    s_y += ys[i];    s_xy += xs[i] * ys[i];
    s_y2 += ys[i] * ys[i]
  }
  return (s_xy - s_x * s_y / n) / (Math.sqrt(s_x2 - s_x * s_x / n) * Math.sqrt(s_y2 - s_y * s_y / n))
}

