const calcFibs = (n) => {
  const result = [1, 1];
  let fibsCount = result.length;
  for (let i = 0; i < n - 2; i++) {
    result.push(result[fibsCount-2] + result[fibsCount-1]);
    fibsCount += 1;
  }
  return result;
}

const fibs = calcFibs(80)
  .map(x => x % 24)
  .map(x => x % 2 ? pure(x) : seq(pure(x), pure(x+2)));


