var chooseInWith = (pat, xs) => {
  xs = xs.map(reify);
  if (xs.length == 0) {
    return silence;
  }
  return pat
    .range(0, xs.length)
    .fmap((i) => xs[Math.floor(i)])
    .innerJoin();
};

var chooseCycles = (...xs) => chooseInWith(rand.segment(1), xs);


