AudioContext.prototype['createClock'] = function (
  callback, // called slightly before each cycle
  duration, // duration of each cycle
  interval = 0.1, // interval between callbacks
  overlap = 0.1, // overlap between callbacks
) {
  let tick = 0; // counts callbacks
  let phase = 0; // next callback time
  let precision = 10 ** 4; // used to round phase
  let minLatency = 0.01;
  const setDuration = (setter) => (duration = setter(duration));
  overlap = overlap || interval / 2;
  const onTick = () => {
    const t = this.currentTime;
    const lookahead = t + interval + overlap; // the time window for this tick
    if (phase === 0) {
      phase = t + minLatency;
    }
    // callback as long as we're inside the lookahead
    while (phase < lookahead) {
      phase = Math.round(phase * precision) / precision;
      phase >= t && callback(phase, duration, tick);
      phase += duration; // increment phase by duration
      tick++;
    }
  };
  let withWorker = true;
  console.log('withWorker', withWorker)
  let intervalID,clock;
  const start = () => {
    console.log('start!');
    if(!withWorker) {
      onTick();
      intervalID = setInterval(onTick, interval * 1000);
    } else {
      clock = new ClockWorker(() => onTick(), interval);
      clock.start();
    }
  };
  const clear = () => withWorker ? clock.stop() : clearInterval(intervalID);
  const pause = () => clear();
  const stop = () => {
    tick = 0;
    phase = 0;
    clear();
  };
  // setCallback
  return { setDuration, start, stop, pause, duration };
};


let cycle;
const tick = (ctx) => (t, duration, tick) => {
  const latency = ((t - ctx.currentTime) * 1000).toFixed(2);
  //console.log(tick, t.toFixed(2), duration, latency);
  t += 0.01;
  const o = ctx.createOscillator();
  const g = ctx.createGain();
  o.frequency.value = /*tick % 4 === 0 ? 440 :*/ 330;
  o.start(t);
  o.stop(t + duration / 2);
  const end = t + duration / 2;
  o.connect(g);
  const attack = 0.01;
  const release = 0.01;
  const max = 0.8;
  g.gain.setValueAtTime(0, t);
  g.gain.linearRampToValueAtTime(max, t + attack);
  g.gain.setValueAtTime(max, end - release);
  g.gain.linearRampToValueAtTime(0, end);
  g.connect(ctx.destination);
};

